def GetRandSpEffect():
    return everySpEffectEver[random.randint(0, everySpEffectEver.Count - 1)]

def GiveSpEffectsToEnemies():
    ene = ex.GetEntityPtrList()
    for e in ene:
        eneChr = Chr.FromAddress(e)
        # print "Chr ID:" + str(eneChr.ID) + " Mdl:" + eneChr.ModelName

        if eneChr.ID < 0:
            eneChr.ID = eneChr.Address

        if eneChr.ID > 0 and eneChr.Nav.Controller.AIController.AnimationID != -1:
            spEffect = GetRandSpEffect()
            f.SetEventSpecialEffect(eneChr.ID, spEffect)
            f.SetEventSpecialEffect_2(eneChr.ID, spEffect)

